import React from 'react'
import axios from 'axios'
import Contant from './Constant.json'

export default function Login() {
   
  const handleLoginSubmit = async (event) => {
    event.preventDefault();

    try {
     const result = await axios.post(
      Contant.API_ADDRESS + '/loginForJWT',
      null,
     {
       auth:  {
        username: event.target.username.value,
        password: event.target.password.value
        }
       }
     );
     console.log(result);
     const recievedJWT = result.data.token;

    } catch(error) {
      console.error(error);
    }
 }

 let loginUIControls = null;
  switch(loginProcessState) {
    case "idle":
      loginUIControls = <button type="submit">Login</button>
      break;

    case "processing":
      loginUIControls = <span style={{color: 'blue'}}>Processing login...</span>
      break;

    case "success":
      loginUIControls = <span style={{color: 'green'}}>Login successful</span>
      break;

    case "error":
      loginUIControls = <span style={{color: 'red'}}>Error</span>
      break;

    default:
      loginUIControls = <button type="submit">Login</button>
  }


return (
    <>
     <form onSubmit= { handleLoginSubmit}>
      <div>
        Username<br/>
        <input type='text' name='username'/>
      </div>

    <div>
      Password<br/>
      <input type='text' name='password' />
    </div>
    <div>
      { loginUIcontrols }
    </div>




     </form>
  

  </>

  )
}
