import React from 'react'
import {render} from 'react-dom'
import axios, { post } from 'axios';


export default function Restaurant() {


  const [allValues, setAllValues] =this.state({
      type: '',
      price: ''
   });
  
    var handleChange = e => {
    setAllValues({...allValues, [e.target.name]: e.target.value});
    }

    class App extends React.Component {
     
      constructor(props) {
        super(props);
        this.state ={
          file:null
        }
        this.onFormSubmit = this.onFormSubmit.bind(this)
        this.onChange = this.onChange.bind(this)
        this.fileUpload = this.fileUpload.bind(this)
      
    }
  
    onFormSubmit(e){
      e.preventDefault()
      const url = 'http://example.com/upload';
      const formData = new FormData();
      formData.append('file', this.state.file);
      const config = {
        headers: {
          'content-type': 'multipart/form-data'
        }
      }
      post(url, formData, config)
        .then((response) => {
          console.log(response.data);
        })
    }
  
    onChange(e) {
      this.setState({ file: e.target.files[0 ]})
    }





    render() {
     return(
      <>
        <div className='resta'>

         <label>Name:
            <input type='text'/>
         </label>

         <label>Adress:
            <input type='text'/>
         </label>

         <label>Opening hours:
            <input type='text'/>
         </label>

         <form onSubmit={this.onFormSubmit}>
        <h1>File Upload</h1>
        <input type="file" onChange={this.onChange} />
      </form>

       <label>
           Restaurant type:
        <div>
      <select value={allValues.type} name="type" onChange={handleChange} >
        <option value="Buffet">Buffet</option>
        <option value="Fastfood">Fast Food</option>
        <option value="Fastcasual">Fast Casual</option>
        <option value="Casualdining">Casual Dining</option>
        <option value="Finedining">Fine Dining</option>
         </select>
        </div>
     </label>

         <label>
        Price level:
       <div>
       <select value={allValues.price} name="price" onChange={handleChange} >
        <option value="€">€</option>
        <option value="€€">€€</option>
        <option value="€€€">€€€</option>
        <option value="€€€€">€€€€</option>
      </select>
    </div> 
      </label>


     <div>
       <button type="submit">Finish</button>
      </div>
     </div>
    </>
       )
   }
  }
 }
