import React, { useState } from 'react'
import axios from 'axios'
import Constant from './Constant.json'
import { useNavigate } from 'react-router-dom'

export default function Signin() {


  const [ signinProcessState, setSigninProcessState ] = useState("idle")
  const navigate = useNavigate();

 const LoginSignalSubmit = async (event) => {
   event.preventDefault();
   setSigninProcessState("Processing");
  try {
     const result = await axios.post(Constant.API_ADDRESS + '/registerBasic',
      {
         username: event.target.username.value,
         email: event.target.email.value,
         password: event.target.password.value,
      });

     console.log(result);
    setSigninProcessState("success");
     setTimeout(() => {
       setSigninProcessState("idle")
         navigate("/login", { replace: true });
      }, 1500);
    } catch (error) {
      console.error(error);
      setSigninProcessState("error");
      setTimeout(() => setSigninProcessState("idle"), 1500);
    }
  };

 let signinUIControls = null;
 switch(signinProcessState) {
   case "idle":
     signinUIControls = <button type='submit'>Sign in</button>
      break;

      case "processing":
      signinUIControls = <span style={{color: 'blue'}}>Processing signup...</span>
      break;

    case "success":
      signinUIControls = <span style={{color: 'green'}}>User created</span>
      break;

    case "error":
      signinUIControls = <span style={{color: 'red'}}>Error</span>
      break;

    default:
      signinUIControls = <button type="submit">Sign up</button>

 }



  return (
    <div>

    <form onSubmit={LoginSignalSubmit}>
     <div>
       Username <br/>
       <input type='text' name='username' />
     </div>
    <div>
      Email <br/>
      <input type='text' name='email' />
    </div>  
      <div>
         Password <br/>
       <input type='text' name='password' />
    </div>
    <div>
        { signinUIControls }
     </div>
   </form>
 </div>

  )
}
