import React, {useState} from 'react'




export default function Restaurant() {
  const getInitialState = () => {
    const value = "€";
    return value;
  };

     const value = "Buffet";
     return value;
    };

  var [pricevalue, setValue] = useState(getInitialState);
  

  const handleChange = (e) => {
    setValue(e.target.value);



     return (
     <div className="resta">
         <label>Name:
            <input type='text'/>
         </label>

         <label>Adress:
            <input type='text'/>
         </label>

         <label>Opening hours:
            <input type='text'/>
         </label>

         <label>
           Restaurant type:
        <div>
      <select value={pricevalue} onChange={handleChange}>
        <option value="Buffet">Buffet</option>
        <option value="Fastfood">Fast Food</option>
        <option value="Fastcasual">Fast Casual</option>
        <option value="Casualdining">Casual Dining</option>
        <option value="Finedining">Fine Dining</option>
         </select>
        </div>
     </label>

         <label>
        Price level:
       <div>
       <select value={pricevalue} onChange={handleChange}>
        <option value="€">€</option>
        <option value="€€">€€</option>
        <option value="€€€">€€€</option>
        <option value="€€€€">€€€€</option>
      </select>
    </div> 
      </label>
     </div>
       );
     
     }
